mapper.select:
select count(*) from orders



			${field} like '%'||#{search}||'%'


			${field} like #{search}

select R.* from ( select rownum rnum, Q.* from (select * from orders
${field} like '%'||#{search}||'%'
${field} like #{search}
order by order_id desc) Q where
rownum<=#{endNo}
) R where
rnum >= #{startNo}

mapper.update:
update order_item
set review_status='off'
where order_item_id=#{order_item_id}
