mapper.select:
select count(*) from product



			${field} like '%'||#{search}||'%'


			${field} like #{search}

select R.* from ( select rownum rnum, Q.* from (select * from product p join category c on p.category_id=c.category_id
p.${field} like '%'||#{search}||'%'
${field} like #{search}
order by p.product_id desc) Q where
rownum<=#{endNo}
) R where
rnum >= #{startNo}

mapper.update:
update product set product_stock


	= #{product_stock} where product_id=#{product_id}


	= product_stock + #{product_stock} where product_id=#{product_id}


	= product_stock - #{product_stock} where product_id=#{product_id}
